mapper.select:

select mno, mid, mname, mpw, mmail, mhp, mnick, mh, htel, haddr, regdate 
from 
  (
  select rownum rno, mno, mid, mname, mpw, mnick, mmail, mhp, mh, htel, haddr, regdate 
  from tbl_member 
  where mno > 0 
  and rownum <= #{page} * #{perPageNum} 
  order by mno desc
  ) 
where rno > (#{page} - 1) * #{perPageNum}
